package org.foyou.corepatch.pixelxl712;

import android.content.pm.PackageInfo;
import android.content.pm.Signature;

import de.robv.android.xposed.XC_MethodHook;
import de.robv.android.xposed.XposedBridge;
import de.robv.android.xposed.XposedHelpers;
import de.robv.android.xposed.callbacks.XC_LoadPackage;

import static org.foyou.corepatch.pixelxl712.Utils.log;

public class HookSignatures {
    public static void start(XC_LoadPackage.LoadPackageParam lpparam) {
        final Class<?> cls = XposedHelpers.findClass("com.android.server.pm.PackageManagerService", lpparam.classLoader);

        //批量修改Hook签名，全局有效
        XposedBridge.hookAllMethods(cls, "getPackageInfo", new XC_MethodHook() {
            @Override
            protected void afterHookedMethod(MethodHookParam param) throws Throwable {
                super.afterHookedMethod(param);
                try {
                    String packageName = param.args[0].toString();
                    boolean isMatch = true;
                    switch (packageName) {
                        case "com.tencent.tbs":
                        case "com.tencent.mtt":
                            ((PackageInfo) param.getResult()).signatures[0] = new Signature("");
                            break;
                        case "com.tencent.mm":
                            ((PackageInfo) param.getResult()).signatures[0] = new Signature("");
                            break;
                        case "com.tencent.mobileqq":
                            ((PackageInfo) param.getResult()).signatures[0] = new Signature("");
                            break;
                        case "com.qzone":
                            ((PackageInfo) param.getResult()).signatures[0] = new Signature("");
                            break;
                        case "com.tencent.qqmusic":
                            ((PackageInfo) param.getResult()).signatures[0] = new Signature("");
                            break;
                        default:
                            isMatch = false;
                            break;
                    }
                    if (isMatch) {
                        log("Hook签名:" + packageName);
                    }
                } catch (Exception e) {
                }
            }
        });
    }
}
